import {Component} from 'react'
import Cookies from 'js-cookie'

class JobsItemDetails extends Component {
  state = {UpdatedJobDetails: '', SimilarJobsDetails: []}

  componentDidMount() {
    this.getJobDetails()
  }

  getJobDetails = async () => {
    const {match} = this.props

    const {params} = match

    const {id} = params
    const JwtToken = Cookies.get('jwt_token')
    const Url = `https://apis.ccbp.in/jobs/${id}`
    const options = {
      headers: {
        Authorization: `Bearer ${JwtToken}`,
      },
      method: 'GET',
    }
    const response = await fetch(Url, options)
    const data = await response.json()
    console.log(data)

    const fetchedData = {
      jobDetails: {
        companyUrl: data.job_details.company_logo_url,
        companyWebsiteUrl: data.job_details.company_website_url,
        employmentType: data.job_details.employment_type,
        id: data.job_details.id,
        description: data.job_details.job_description,

        lifeAtCompany: {
          description: data.job_details.life_at_company.description,
          imageUrl: data.job_details.life_at_company.description,
          location: data.job_details.life_at_company.description,
          salary: data.job_details.life_at_company.description,
          rating: data.job_details.life_at_company.description,
        },
        skills: data.job_details.skills.map(each => ({
          name: each.name,
          imageUrl: each.image_url,
        })),
        title: data.job_details.title,
      },
      similarJobs: data.similar_jobs.map(each => ({
        id: each.id,
        title: each.title,
        employmentType: each.employment_type,
        jobDescription: each.job_description,
        location: each.location,
        rating: each.rating,
      })),
    }

    console.log(fetchedData)
    this.setState({UpdatedJobDetails: fetchedData.jobDetails})
  }

  render() {
    const {UpdatedJobDetails} = this.state
    console.log(UpdatedJobDetails)
    const {
      employmentType,
      companyWebsiteUrl,
      companyUrl,
      id,
      description,
      title,
      lifeAtCompany,
      skills,
    } = UpdatedJobDetails
    // console.log(employmentType)
    // const {salary} = lifeAtCompany
    // console.log(lifeAtCompany)
    // console.log(skills)
    //
    // console.log(title)

    return (
      <div>
        <img src={companyUrl} />
        <h1>{title}</h1>
        <p>{employmentType}</p>
        <p>lll</p>
      </div>
    )
  }
}
export default JobsItemDetails
