import {Component} from 'react'
import Cookies from 'js-cookie'

class JobsItemDetails extends Component {
  state = {}

  componentDidMount() {
    this.getJobDetails()
  }

  getJobDetails = async () => {
    const {match} = this.props

    const {params} = match

    const {id} = params
    const JwtToken = Cookies.get('jwt_token')
    const Url = `https://apis.ccbp.in/jobs/${id}`
    const options = {
      headers: {
        Authorization: `Bearer ${JwtToken}`,
      },
      method: 'GET',
    }
    const response = await fetch(Url, options)
    const data = await response.json()
    console.log(data)

    const fetchedData = {
      jobDetails: {
        companyUrl: data.job_details.company_logo_url,
        companyWebsiteUrl: data.job_details.company_website_url,
        employmentType: data.job_details.employment_type,
        id: data.job_details.id,
        description: data.job_details.job_description,

        lifeAtCompany: {
          description: data.job_details.life_at_company.description,
          imageUrl: data.job_details.life_at_company.image_url,
          location: data.job_details.life_at_company.location,
          salary: data.job_details.life_at_company.package_per_annum,
          rating: data.job_details.life_at_company.rating,
        },
        skills: data.job_details.skills.map(each => ({
          name: each.name,
          imageUrl: each.image_url,
        })),
        title: data.job_details.title,
      },
      similarJobs: data.similar_jobs.map(each => ({
        id: each.id,
        title: each.id,
        employmentType: each.employment_type,
        jobDescription: each.job_description,
        location: each.location,
        rating: each.rating,
        similarTitle: each.title,
      })),
    }

    console.log(fetchedData)
  }

  render() {
    return <h1>lllllllllllllllllll</h1>
  }
}
export default JobsItemDetails
